CREATE ALGORITHM=UNDEFINED DEFINER=`stokaboka`@`192.168.10.30` SQL SECURITY DEFINER VIEW `v_employees_groups` AS select `eg`.`id` AS `id`,`eg`.`id_employee` AS `id_employee`,`eg`.`id_group` AS `id_group`,`eg`.`id_role` AS `id_role`,`eg`.`from_date` AS `from_date`,`eg`.`to_date` AS `to_date`,`eg`.`in_from_group` AS `in_from_group`,`eg`.`out_to_group` AS `out_to_group`,`eg`.`status` AS `status`,`eg`.`role_status` AS `role_status`,`e`.`fam` AS `fam`,`e`.`im` AS `im`,`e`.`ot` AS `ot`,concat_ws(_utf8' ',`e`.`fam`,`e`.`im`,`e`.`ot`) AS `full_fam`,concat_ws(_utf8' ',`e`.`fam`,left(`e`.`im`,1),_utf8'.',left(`e`.`ot`,1),_utf8'.') AS `dot_fam`,`e`.`nickname` AS `nickname`,`g`.`id_level_1` AS `id_level_1`,`g`.`id_level_2` AS `id_level_2`,`g`.`id_level_3` AS `id_level_3`,`g`.`id_level_4` AS `id_level_4`,`g`.`from_date` AS `groups_from_date`,`g`.`to_date` AS `groups_to_date`,`g`.`name` AS `name`,`g`.`name_level_1` AS `name_level_1`,`g`.`name_level_2` AS `name_level_2`,`g`.`name_level_3` AS `name_level_3`,`g`.`name_level_4` AS `name_level_4`,`r`.`name` AS `role_name`,`r`.`short` AS `role_short_name` from (((`employees_groups` `eg` left join `employees` `e` on((`eg`.`id_employee` = `e`.`id`))) left join `groups` `g` on((`eg`.`id_group` = `g`.`id`))) left join `roles` `r` on((`eg`.`id_role` = `r`.`id`)))